<?php include 'kon/kon.php';if (trim($_POST['note_id']) ==''){$error[]= '- note_id harus diisi';}if (trim($_POST['subtask_id']) ==''){$error[]= '- subtask_id harus diisi';}if (trim($_POST['note']) ==''){$error[]= '- note harus diisi';}$note_id=$_POST['note_id'];$subtask_id=$_POST['subtask_id'];$note=$_POST['note'];if (isset($error)){echo implode("<br />", $error);}else{try{$pdo = new PDOx();$dbh=$pdo->getKoneksi();$dbh->setAttribute( PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION );$sql="INSERT INTO table_note(note_id,subtask_id,note)VALUES (:note_id,:subtask_id,:note)"; $q = $dbh->prepare($sql); $q->execute(array('note_id'=>$note_id,'subtask_id'=>$subtask_id,'note'=>$note)); echo "Succes add data!";} catch(PDOException $e){echo "Error". $e->getMessage(); exit;}}?>